import React, { useCallback, useMemo } from "react";
import ReactFlow, { Background, Controls, MiniMap } from "reactflow";
import "reactflow/dist/style.css";

const getNodeColor = (value) => {
  if (value === null) return "#ffb74d";
  if (Array.isArray(value)) return "#43a047";
  if (typeof value === "object") return "#6a1b9a";
  return "#fb8c00";
};

const CustomNode = ({ data }) => (
  <div
    style={{
      padding: 8,
      borderRadius: 8,
      background: data.bg,
      color: "#fff",
      textAlign: "center",
      minWidth: 90,
      cursor: "pointer",
    }}
  >
    <div style={{ fontWeight: 700 }}>{data.label}</div>
  </div>
);

function FlowTree({ nodes, edges, rfInstanceRef, onNodeClick }) {
  const nodeTypes = useMemo(() => ({ custom: CustomNode }), []);

  const enriched = nodes.map((n) => {
    const val = n.data?.value;
    return { ...n, type: "custom", data: { ...n.data, bg: getNodeColor(val) } };
  });

  const handleNodeClick = useCallback(
    (evt, node) => {
      onNodeClick?.(evt, node);
    },
    [onNodeClick]
  );

  return (
    <div className="h-full w-full">
      <ReactFlow
        nodes={enriched}
        edges={edges.map((e) => ({
          ...e,
          type: "smoothstep",
          animated: true,
          style: { stroke: "#8884d8", strokeWidth: 2 },
        }))}
        nodeTypes={nodeTypes}
        onNodeClick={handleNodeClick}
        fitView
        fitViewOptions={{ padding: 0.3 }}
        onInit={(instance) => {
          if (rfInstanceRef) rfInstanceRef.current = instance;
        }}
      >
        <Background />
        <MiniMap />
        <Controls />
      </ReactFlow>
    </div>
  );
}

export default FlowTree;
