export function jsonToFlow(rootObj) {
  const nodes = [];
  const edges = [];
  let counter = 0;

  function visit(value, path = '$', depth = 0, label = null, parent = null) {
    const id = path;
    const nodeLabel = label === null ? 'root' : String(label);
    const raw =
      typeof value === 'object' && value !== null
        ? JSON.stringify(value)
        : String(value);
    const position = { x: depth * 220, y: counter * 80 };
    counter++;

    nodes.push({
      id,
      position,
      data: { label: nodeLabel, raw, path: id, value },
    });

    if (parent) {
      edges.push({
        id: `e-${parent}-${id}`,
        source: parent,
        target: id,
        animated: true,
      });
    }

    if (value !== null && typeof value === 'object') {
      if (Array.isArray(value)) {
        value.forEach((item, idx) =>
          visit(item, `${path}[${idx}]`, depth + 1, `[${idx}]`, id)
        );
      } else {
        Object.keys(value).forEach((k) =>
          visit(value[k], `${path}.${k}`, depth + 1, k, id)
        );
      }
    }
  }

  visit(rootObj, '$', 0, null, null);
  return { nodes, edges };
}
