import React, { useState, useCallback, useRef } from "react";
import FlowTree from "./components/FlowTree";
import { jsonToFlow } from "./utils/jsonToFlow";

export default function App() {
  const [jsonText, setJsonText] = useState("");
  const [nodes, setNodes] = useState([]);
  const [edges, setEdges] = useState([]);
  const [status, setStatus] = useState("");
  const rfInstanceRef = useRef(null);

  const handleVisualize = useCallback(() => {
    try {
      const parsed = JSON.parse(jsonText);
      const { nodes: n, edges: e } = jsonToFlow(parsed);

      const nodesWithValue = n.map((nd) => ({
        ...nd,
        data: {
          ...nd.data,
          value: (() => {
            try {
              return JSON.parse(nd.data.raw);
            } catch {
              return nd.data.raw;
            }
          })(),
        },
      }));

      setNodes(nodesWithValue);
      setEdges(e);
      setStatus("Tree visualized");
      setTimeout(() => setStatus(""), 1400);

      // ðŸ”¹ Auto-center tree after visualize
      setTimeout(() => rfInstanceRef.current?.fitView({ padding: 0.3 }), 300);
    } catch {
      setStatus("Invalid JSON. Fix format.");
    }
  }, [jsonText]);

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className="w-1/3 p-4 border-r flex flex-col gap-3 bg-gray-50">
        <h2 className="text-lg font-semibold mb-2">JSON Input</h2>
        <textarea
          className="border rounded p-2 flex-1 font-mono text-sm"
          value={jsonText}
          onChange={(e) => setJsonText(e.target.value)}
          placeholder="Paste JSON here..."
        />

        <button
          onClick={handleVisualize}
          className="bg-blue-600 text-white rounded py-2 hover:bg-blue-700"
        >
          Visualize
        </button>

        {status && <div className="text-sm text-gray-600">{status}</div>}

        {/* Sidebar Zoom Controls */}
        <div className="flex gap-2 mt-3">
          <button
            onClick={() => rfInstanceRef.current?.zoomIn()}
            className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300"
          >
            Zoom In
          </button>
          <button
            onClick={() => rfInstanceRef.current?.zoomOut()}
            className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300"
          >
            Zoom Out
          </button>
          <button
            onClick={() => rfInstanceRef.current?.fitView({ padding: 0.3 })}
            className="px-3 py-1 bg-gray-200 rounded hover:bg-gray-300"
          >
            Fit View
          </button>
        </div>
      </div>

      {/* Tree Visualizer */}
      <div className="flex-1 border rounded overflow-hidden">
        <FlowTree
          nodes={nodes}
          edges={edges}
          onNodeClick={(e, node) => console.log("Clicked:", node)}
          rfInstanceRef={rfInstanceRef}
        />
      </div>
    </div>
  );
}
