export function jsonToFlow(rootObj) {
  let nodes = [];
  let edges = [];
  let counter = 0;

  function visit(value, path = "$", depth = 0, label = null, parent = null) {
    const cleanId = path;
    const nodeLabel = label === null ? "root" : String(label);
    const raw =
      typeof value === "object" && value !== null
        ? JSON.stringify(value)
        : String(value);
    const position = { x: depth * 240, y: counter * 90 };
    counter += 1;

    nodes.push({
      id: cleanId,
      position,
      data: { label: nodeLabel, raw, path: cleanId, value },
      style: { boxShadow: "none" },
    });

    if (parent) {
      edges.push({
        id: `e-${parent}-${cleanId}`,
        source: parent,
        target: cleanId,
      });
    }

    if (value !== null && typeof value === "object") {
      if (Array.isArray(value)) {
        value.forEach((item, idx) =>
          visit(item, `${path}[${idx}]`, depth + 1, `[${idx}]`, cleanId)
        );
      } else {
        Object.keys(value).forEach((k) =>
          visit(value[k], `${path}.${k}`, depth + 1, k, cleanId)
        );
      }
    }
  }

  visit(rootObj, "$", 0, null, null);
  return { nodes, edges };
}
