import React, { useCallback, useMemo } from "react";
import ReactFlow, { Background, Controls, MiniMap, Handle } from "reactflow";
import "reactflow/dist/style.css";

const getNodeColor = (value) => {
  if (value === null) return "#ffb74d";
  if (Array.isArray(value)) return "#43a047";
  if (typeof value === "object") return "#6a1b9a";
  return "#fb8c00";
};

const CustomNode = ({ data }) => {
  return (
    <div
      className="relative group"
      style={{
        padding: 8,
        borderRadius: 8,
        background: data.bg,
        color: "#fff",
        textAlign: "center",
        minWidth: 100,
        cursor: "pointer",
        fontSize: 13,
        position: "relative",
      }}
    >
      <div style={{ fontWeight: 700 }}>{data.label}</div>

      {/* Hover tooltip */}
      <div
        className="absolute z-10 hidden group-hover:block bg-black text-white text-xs rounded px-2 py-1"
        style={{
          top: "110%",
          left: "50%",
          transform: "translateX(-50%)",
          whiteSpace: "nowrap",
        }}
      >
        <div>
          <strong>Path:</strong> {data.path}
        </div>
        <div>
          <strong>Value:</strong>{" "}
          {typeof data.value === "object"
            ? JSON.stringify(data.value)
            : String(data.value)}
        </div>
      </div>
    </div>
  );
};

export default function FlowTree({ nodes, edges, onNodeClick }) {
  const nodeTypes = useMemo(() => ({ custom: CustomNode }), []);

  const enriched = nodes.map((n) => {
    const val = n.data?.value;
    return {
      ...n,
      type: "custom",
      data: { ...n.data, bg: getNodeColor(val) },
    };
  });

  const handleNodeClick = useCallback(
    (evt, node) => {
      onNodeClick(evt, node);
    },
    [onNodeClick]
  );

  return (
    <div className="h-full w-full">
      <ReactFlow
        nodes={enriched}
        edges={edges}
        nodeTypes={nodeTypes}
        onNodeClick={handleNodeClick}
        fitView
      >
        <Background />
        <MiniMap />
        <Controls showZoom={false} showFitView={false} />
      </ReactFlow>
    </div>
  );
}
