import React from "react";

export default function JsonInput({
  jsonText,
  setJsonText,
  onVisualize,
  onClear,
  onDownload,
  samplePlaceholder,
}) {
  return (
    <div className="flex flex-col h-full p-3">
      <label className="font-semibold mb-2 text-sm">Enter JSON Data:</label>

      {/* JSON Textarea */}
      <textarea
        className="flex-1 w-full p-2 border rounded-md bg-white dark:bg-slate-800 dark:text-slate-100 resize-none text-sm"
        placeholder={samplePlaceholder}
        value={jsonText}
        onChange={(e) => setJsonText(e.target.value)}
      />

      {/* Action Buttons */}
      <div className="flex justify-end gap-2 mt-3">
        <button
          onClick={onClear}
          className="px-3 py-1.5 bg-gray-400 hover:bg-gray-500 text-white text-sm rounded-md transition"
        >
          Reset
        </button>

        <button
          onClick={onVisualize}
          className="px-4 py-1.5 bg-blue-600 hover:bg-blue-700 text-white text-sm font-semibold rounded-md transition"
        >
          Generate Tree
        </button>
      </div>
    </div>
  );
}
